<template>
  <div v-if="code">
    <div ref="svg" id="qrcode" class="text-center" />
    <template v-if="showCode">
      <p class="text-center">{{ text }}</p>
      <v-text-field v-model="code" readonly @focus="$event.target.select()" hide-details />
    </template>
  </div>
</template>

<script lang="ts" setup>
  import { coreStore } from '@/stores/app'
  import QRCodeStyling from 'qr-code-styling'

  const imageData = 'data:image/svg+xml;base64,'

  const store = coreStore()
  const router = useRouter()
  let codeWriter: QRCodeStyling

  const svg = useTemplateRef('svg')

  export interface StyledQRCodeProps {
    baseUrl?: string
    text: string
    showCode?: boolean
    isTrialCode?: boolean
  }

  const compProps = withDefaults(defineProps<StyledQRCodeProps>(), {
    isTrialCode: true,
    showCode: true,
  })

  const code = computed(() => {
    if (compProps.text) {
      if (compProps.isTrialCode) {
        // @ts-ignore
        const uuidPart = router.resolve({ path: `/trial/import/${compProps.text}` }).path

        if (compProps.baseUrl) {
          return compProps.baseUrl + '#' + uuidPart
        } else {
          let origin = window.location.origin
          let pathname = window.location.pathname

          if (origin.lastIndexOf('/') === origin.length - 1) {
            origin = origin.slice(0, -1)
          }
          if (pathname.length > 0 && pathname[0] === '/') {
            pathname = pathname.slice(1)
          }
          if (pathname.lastIndexOf('/') === pathname.length - 1) {
            pathname = pathname.slice(0, -1)
          }

          if (pathname.length > 0) {
            pathname = pathname + '/'
          }

          return origin + '/' + pathname + '#' + uuidPart
        }
      } else {
        return compProps.text
      }
    } else {
      return undefined
    }
  })

  watch(code, async () => updateBarcode())

  function updateBarcode () {
    const svgElement = svg.value
    if (code.value && svgElement) {
      nextTick(() => {
        if (!codeWriter) {
          codeWriter = new QRCodeStyling({
            margin: store.storeIsDarkMode ? 20 : 0,
            width: 300,
            height: 300,
            data: code.value,
            // type: 'svg', // We don't use the SVG option, because we want people to be able to right-click and copy QR codes.
            dotsOptions: {
              type: 'rounded',
            },
            imageOptions: {
              hideBackgroundDots: true,
              imageSize: 0.4,
              margin: 0,
            },
            image: imageData,
          })
          codeWriter.append(svgElement)
        } else {
          codeWriter.update({
            data: code.value,
          })
          codeWriter.append(svgElement)
        }
      })
    } else {
      // TODO
    }
  }

  onMounted(() => updateBarcode())
</script>

<style>
</style>
